import React, {useState} from 'react';
import useFetch from "../checkin/config/useFetch";
import Loader from "../loader/Loader";
import SectorList from "./SectorList";

const AddSector = () => {
    const {loading, error, data} = useFetch({url: "api/protected/users/sectors/me"});
    const restult = useFetch({url: "api/public/sectors"});

    if (loading || restult.loading) {
        return <Loader className="page-loader"/>
    }


    const [name, setName] = useState('');
    const [sectors, setSectors] = useState(restult);
    const [selectedSectors, setSelectedSectors] = useState([]);
    const [agreedTerms, setAgreedTerms] = useState(false);
    const [formData, setFormData] = useState(null);


    const handleChange = (e) => {
        e.preventDefault();

    }

    const handleSubmit = (e) => {
        e.preventDefault();
    }
    return (
        <div>
            <h4 className="card-title">Sector Registration</h4>
            <h6 className="card-subtitle">Register yourself under specific sectors of the company</h6><br/>
            <form onSubmit={handleSubmit}>
                <div className="row">
                    <div className="form-group form-group--left fm-input-width">
                        <label>Name</label>
                        <input type="text" className="form-control fm-input-width" name="name" value={name} onChange={(e) => setName(e.target.value)}/>
                        <i className="form-group__bar fm-input-width"/>
                    </div>
                    <br/>

                    <div className="mb-2">
                        <div className="checkbox checkbox--char">
                            <input type="checkbox" name="agreeTerms" id="agreedTerms" value={agreedTerms} onChange={(e) =>setAgreedTerms(e.target.value)}/>
                            <label htmlFor="agreeTerms" className="bg-red checkbox__char">A</label> Agree to terms
                        </div>
                    </div>

                    <button type="submit" className="btn login__block__btn">Save</button>
                </div>
            </form>

            {(!error && data.length > 0) ? (<SectorList {...data[0]}/>) : ''}
        </div>
    );
};

export default AddSector;
