import React, {useState} from 'react';
import useFetch from "../checkin/config/useFetch";
import Loader from "../loader/Loader";
import SectorList from "./SectorList";
import Alert from "../alert/Alert";
import axiosApi from "../checkin/config/axios.client";
import {SectorLevel} from "../constants/sectorlevels.enum";

const AddSector = () => {
    const {loading, error, data} = useFetch({url: "api/protected/users/sectors/me"});
    const result = useFetch({url: "api/public/sectors"});

    const [name, setName] = useState('');
    const [sectors, setSectors] = useState(result.data);
    const [selectedSectors, setSelectedSectors] = useState([]);
    const [agreedTerms, setAgreedTerms] = useState(false);
    const [isLoading, setIsLoading] = useState(false);
    const [isError, setIsError] = useState(false);
    const [message, setMessage] = useState("");

    if (loading || result.loading || isLoading) {
        return <Loader className="page-loader"/>
    }

    //  alert(JSON.stringify(result.data));

    const handleSectorsChange = (event) => {
        event.preventDefault();
        const selectedOptions = Array.from(event.target.selectedOptions, (option) => option.value);
        alert(JSON.stringify(selectedOptions))
        setSelectedSectors(selectedOptions);
    };
    const getAuthUser = () => {
        return JSON.parse(localStorage.getItem('user'));
    }

    const handleSubmit = async (e) => {
        e.preventDefault();
        if ((name.length === 0 && sectors.length === 0 && !agreedTerms)) {
            return;
        }
        if (validateInput()) {
            const user = getAuthUser();
            await createSector({userId: user.id, name: name, sectors: selectedSectors, agreeTerms: agreedTerms})
        } else {
            setMessage("All form fields are required");
            setIsError(true);
        }
    }

    const createSector = async (body) => {
        setIsLoading(true);
        try {
            await axiosApi({'method': 'POST', 'url': "api/protected/users/sectors", data: body});
            setIsLoading(false);
            //window.location.reload();
        } catch (error) {
            setIsError(true);
            setMessage("An unexpected error occurs while adding your selected sector information")
            setIsLoading(false);
        }
    }

    const validateInput = () => {
        return (name.length > 0 /*&& sectors.length > 0*/ && agreedTerms);
    }

    const options = [];
    result.data.map((sector) => {
        options.push({
            value: (sector.id + '-' + SectorLevel.SECTOR_CATEGORY),
            label: sector.name,
            isDisabled: (sector.subSectors.length > 0)
        });
        sector.subSectors.map((subSector) => {
            options.push({
                value: (subSector.id + '-' + SectorLevel.SUB_SECTOR),
                label: '\u00A0\u00A0\u00A0\u00A0' + subSector.name,
                isDisabled: (subSector.minorSectors.length > 0)
            });
            subSector.minorSectors.map((minorSector) => {
                options.push({
                    value: (minorSector.id + '-' + SectorLevel.MINOR_SECTOR),
                    label: '\u00A0\u00A0\u00A0\u00A0\u00A0\u00A0\u00A0\u00A0' + minorSector.name,
                    isDisabled: (minorSector.sectors.length > 0)
                });
                minorSector.sectors.map((value) => {
                    options.push({
                        value: (value.id + '-' + SectorLevel.SECTOR),
                        label: '\u00A0\u00A0\u00A0\u00A0\u00A0\u00A0\u00A0\u00A0\u00A0\u00A0\u00A0\u00A0' + value.name
                    });

                })
            })
        })
    });
    alert(JSON.stringify(options));

    // let optionsars = result.data.map((sector) => {
    //     let content = (<option key={sector.id + '-' + SectorLevel.SECTOR_CATEGORY}
    //                            value={sector.id + '-' + SectorLevel.SECTOR_CATEGORY}
    //                            disabled={sector.subSectors.length > 0}>{sector.name}</option>);
    //     if (sector.subSectors.length === 0) {
    //         return content;
    //     } else {
    //         return [content, ...sector.subSectors.map((subSector) => {
    //             let subContent = (<option key={subSector.id + '-' + SectorLevel.SUB_SECTOR}
    //                                       value={subSector.id + '-' + SectorLevel.SUB_SECTOR}
    //                                       disabled={subSector.minorSectors.length > 0}>&nbsp;&nbsp;&nbsp;&nbsp; {subSector.name}</option>);
    //             if (subSector.minorSectors.length === 0) {
    //                 return subContent;
    //             } else {
    //                 return [subContent, ...subSector.minorSectors.map((minorSector) => {
    //                     let minorContent = (<option key={minorSector.id + '-' + SectorLevel.MINOR_SECTOR}
    //                                                 value={minorSector.id + '-' + SectorLevel.MINOR_SECTOR}
    //                                                 disabled={minorSector.sectors.length > 0}>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;{minorSector.name}</option>);
    //                     if (minorSector.sectors.length === 0) {
    //                         return minorContent;
    //                     } else {
    //                         return [minorContent, ...minorSector.sectors.map((value) => {
    //                             return (<option key={value.id + '-' + SectorLevel.SECTOR}
    //                                             value={value.id + '-' + SectorLevel.SECTOR}>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;{value.name}</option>)
    //                         })];
    //                     }
    //                 })]
    //             }
    //         })]
    //     }
    // })
    return (
        <div>
            <h4 className="card-title">Sector Registration</h4>
            <Alert className="alert alert-danger"
                   message={message}
                   show={isError} reset={setIsError}/><br/>
            <form onSubmit={handleSubmit}>
                <div className="form-group form-group--left fm-input-width">
                    <label>Name</label>
                    <input type="text" className="form-control fm-input-width" name="name" value={name}
                           onChange={(e) => setName(e.target.value)}/>
                    <i className="form-group__bar fm-input-width"/>
                </div>
                {result.data.length > 0 ? <div>
                        <br/><br/>
                        <div className="form-group form-group--left fm-input-width">
                            <label>Sectors</label>
                            <select
                                multiple
                                value={selectedSectors}
                                className="form-control fm-input-width"
                                onChange={handleSectorsChange}
                                size="100"
                            >

                            </select>
                        </div>
                    </div>
                    : ''
                }
                <br/><br/>
                <div className="mb-2">
                    <input type="checkbox" name="agreeTerms"
                           onChange={() => setAgreedTerms(!agreedTerms)} checked={agreedTerms}/> Agree to terms
                </div>
                <br/><br/>
                <button type="submit" className="btn login__block__btn">Save</button>
            </form>

            {(!error && data.length === 0) ? (<SectorList {...data[0]}/>) : ''}
        </div>
    );
};

export default AddSector;
